mapper.select:
SELECT 
FROM order_info


        AND user_id = #{condition.userId}


        AND store_id = #{condition.storeId}


        AND order_no = #{condition.orderNo}


        AND order_status = #{condition.orderStatus}


ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM order_info


        AND user_id = #{condition.userId}


        AND store_id = #{condition.storeId}


        AND order_no = #{condition.orderNo}


        AND order_status = #{condition.orderStatus}

mapper.insert:
INSERT INTO order_info (
    order_no, user_id, store_id, shipping_id, total_amount, actual_amount,
    discount_amount, shipping_fee, order_status, payment_method, order_note, order_items, 
    create_time, update_time
) VALUES (
    #{orderNo}, #{userId}, #{storeId}, #{shippingId}, #{totalAmount}, #{actualAmount},
    #{discountAmount}, #{shippingFee}, #{orderStatus}, #{paymentMethod}, #{orderNote}, #{orderItems}, 
    #{createTime}, #{updateTime}
)

mapper.update:
UPDATE order_info
order_status = #{orderStatus}, payment_method = #{paymentMethod}, pay_time = #{payTime}, ship_time = #{shipTime}, complete_time = #{completeTime}, cancel_time = #{cancelTime}, update_time = #{updateTime}
WHERE order_id = #{orderId}

